    private function fetchDistinctDate($table): array
    {
        $dar   = $this->disk_usage_dao->retrieve("SELECT distinct date FROM $table ORDER BY date");
        $dates = [];

        foreach ($dar as $row) {
            $date    = explode(' ', $row['date']);
            $dates[] = $date[0];
        }

        return $dates;
    }
