declare(strict_types=1);

class User_SSHKeyValidator_KeyValidationTest extends \PHPUnit\Framework\TestCase // phpcs:ignore
{
    use \Tuleap\GlobalResponseMock;
    use \Tuleap\GlobalLanguageMock;

    /** @var PFUser*/
    protected $user;
    /**
     * @var string
     */
    protected $key1;
    /**
     * @var string
     */
    protected $key2;
    /**
     * @var User_SSHKeyValidator
     */
    protected $validator;

    #[\Override]
    protected function setUp(): void
    {
        $this->key1 = 'ssh-rsa  user@shunt';
        $this->key2 = 'ssh-rsa  user@crampons';

        $this->user      = new PFUser([
            'language_id' => 'en_US',
        ]);
        $this->validator = new User_SSHKeyValidator();
    }

    public function testItDoesntRaiseAnErrorWhenTheKeyIsValid(): void
    {
        $GLOBALS['Response']->expects($this->never())->method('addFeedback');

        $this->assertEquals(
            [$this->key1],
            $this->validator->validateAllKeys([$this->key1])
        );
    }

    public function testItDoesntRaiseAnErrorWhenAllTheKeysAreValid(): void
    {
        $GLOBALS['Response']->expects($this->never())->method('addFeedback');

        $this->assertEquals(
            [$this->key1, $this->key2],
            $this->validator->validateAllKeys([
                $this->key1,
                $this->key2,
            ])
        );
    }

    public function testItRaisesAWarningWhenTheKeyIsInvalid(): void
    {
        $keys = ['bla'];

        $GLOBALS['Response']->expects($this->once())->method('addFeedback')->with('warning');

        $this->assertCount(0, $this->validator->validateAllKeys($keys));
    }

    public function testItRaisesAWarningWhenTheKeyIsNotValidOutsideAnAuthorizedKeysFile(): void
    {
        $keys = [
            'tuleap.example.com,192.0.2.1 ssh-rsa ',
        ];

        $GLOBALS['Response']->expects($this->once())->method('addFeedback')->with('warning');

        $this->assertCount(0, $this->validator->validateAllKeys($keys));
    }

    public function testItRaisesAWarningWhenThePublicKeyContentIsNotValid(): void
    {
        $GLOBALS['Response']->expects($this->once())->method('addFeedback')->with('warning');

        $keys = [
            'ssh-rsa ',
        ];

        $this->assertCount(0, $this->validator->validateAllKeys($keys));
    }

    public function testItRaisesAWarningWhenTheKeyIsInvalidAmongValidKeys(): void
    {
        $keys = [
            $this->key1,
            'bla',
            $this->key2,
        ];

        $GLOBALS['Response']->expects($this->once())->method('addFeedback')->with('warning');

        $this->assertEquals([$this->key1, $this->key2], $this->validator->validateAllKeys($keys));
    }

    public function testItRaisesAWarningWhenTheSameKeyIsAddedTwice(): void
    {
        $keys = [
            $this->key1,
            $this->key1,
        ];

        $GLOBALS['Response']->expects($this->once())->method('addFeedback')->with('warning');

        $this->assertCount(1, $this->validator->validateAllKeys($keys));
    }
}
